                    'admin_celular' => $this->input->post('admin_celular'),
                    'admin_telefono' => $this->input->post('admin_telefono'),
                    'tipousuario_id' => $this->input->post('tipousuario_id'),
                    'admin_login' => $this->input->post('admin_login'),
